import axios from "axios";
import Pane from "Pane.vue";
export default {
  components: {
    Pane: Pane
  },
  data: function data() {
    return {
      menu: {
        discipline: "",
        rep: "",
        set: "",
        weight: "",
        calorie: ""
      },
      errors: ""
    };
  },
  methods: {
    createmenu: function createmenu() {
      var _this = this;

      axios.post("/api/v1/menus", this.menu).then(function (response) {
        var e = response.data;

        _this.$router.push({
          name: "Detail",
          params: {
            id: e.id
          }
        });
      })["catch"](function (error) {
        console.error(error);

        if (error.response.data && error.response.data.errors) {
          _this.errors = error.response.data.errors;
        }
      });
    }
  }
};
